import { EducationModel } from '../model/education';
import './educationComponentCSS.css'
import { useState, useEffect } from 'react';

function Education() {

    const [mounted, setMounted] = useState(false);

    useEffect(() => {
        setMounted(true);

        return () => {
            setMounted(false);
        };
    }, []);

    const [educationFormGroup, setEducationFormGroup] = useState([{}]);

    function addEducation() {
        setEducationFormGroup([...educationFormGroup, {}])
        console.log('add education plus clicked')
    }

    const [educations, setEducations] = useState([{}])

    useEffect(() => {
        console.log('in use effect')
        if (mounted) {
            const getEducations = async() => {
                console.log('in function')
                let educationModel = new EducationModel()

                try {
                    // let educationModel = new EducationModel()
                    let educations = await educationModel.getEducation()
                    console.log('here')
                    console.log(`educations ${educations[0].universityName}`)
                    setEducations(educations)
                }
                catch (error) {
                    console.log('here2');
                    setEducations(null)
                }
            }
            getEducations();
        }

    }, [mounted])



    console.log(educations)

    return (
        <div class='container'>
        <form>
            <div class='education-border'>
                <h1 class='education'>Education</h1>
                <hr></hr>

                {/* {educationFormGroup.length > 1 ? (<hr></hr>) : (null)} */}
                {educations.map((education) => (
                    <div>
                    {educationFormGroup.map(() => (

                    // The root inside this map has to be a single element - so like a div.
                        <div class='educationFormGroup'>
                            {/* {educationFormGroup.length > 1 || educations.length > 1 ? (<hr class='educationDivider'></hr>) : (null)} */}

                            <div class='form-group'>
                                <label>University Name</label>
                                {console.log(`universityName ${education.universityName}`)}
                                <input style = {{width: `${education.universityName.length}ch`}} type="text" placeholder={educations == null ? 'University Name' : null} value = {educations != null ? education.universityName : null}/>
                            </div>

                            {/* <div class='form-group'>
                                <label>Degree</label>
                                <input style = {{width: `${education.degree.length}ch`}} type="text" placeholder={educations == null ? 'Degree' : null} value = {educations != null ? education.degree : null}/>
                            </div> */}

                            {/* change to date picker */}
                            {/* <div class='form-group'>
                                <label>Start Date</label>
                                <input style = {{width: `${education.startDateMonth.length}ch`}} type="text" placeholder={educations == null ? 'Start Date' : null} value = {educations != null ? education.startDateMonth : null}/>
                            </div> */}

                            {/* change to date picker */}
                            {/* <div class='form-group'>
                                <label>End Date</label>
                                <input type="text" placeholder='End Date'/>
                            </div> */}

                            {/* <div class='form-group'>
                                <label>Currently Study Here</label>
                                <input type="checkbox"/>
                            </div>

                            <div class='form-group'>
                                <label>City, State</label>
                                <input type="text" placeholder="City, State"/>
                            </div> */}

                            {educationFormGroup.length > 1 || educations.length > 1 ? (<hr class='educationDivider'></hr>) : (null)}

                        </div>
                    ))}
                    </div>

                )) }


                {console.log(educationFormGroup.length)}



                <h1 id='plus' onClick={()=>addEducation()}>+</h1>

            </div>
        </form>

    </div>
    )
}

export default Education;
