import { EducationModel } from '../model/education';
import './educationComponentCSS.css'
import { useState, useEffect } from 'react';

function Education() {

    const [educationFormGroup, setEducationFormGroup] = useState([{}]);

    function addEducation() {
        // setEducationFormGroup([...educationFormGroup, {}])
        setEducations([...educations, {
            "universityName": "",
            "degree": "",
            "startDateMonth": "",
            "startDateYear": "",
            "endDateMonth": "",
            "endDateYear": "",
            "currentlyWorkHere": "",
            "locationState": "",
            "new": true
        }])
        // console.log(educationFormGroup)
        // console.log(educationFormGroup[educationFormGroup.length-1])
        console.log('add education plus clicked')
    }

    // it was displaying universityName as undefined because the component was first being rendered
    // with an empty object. But, by initially setting the state of educations to null, in the render
    // code, I can check if educations is not null, then display the render.
    const [educations, setEducations] = useState(null)

    useEffect(() => {
        console.log('in use effect')
            const getEducations = async() => {
                console.log('in function')
                let educationModel = new EducationModel()

                try {
                    // let educationModel = new EducationModel()
                    let educations = await educationModel.getEducation()
                    console.log('here')
                    console.log(`educations ${educations[0].universityName}`)
                    setEducations(educations)
                }
                catch (error) {
                    console.log('here2');
                    setEducations(null)
                }
            }
            getEducations();


    }, [])



    console.log(educations)

    function handleCheckboxChange(id, checked) {
        console.log(`checked ${checked}`)
        // target.setAttribute('checked', 'false')
        // console.log(`target ${target}`)

        //first, find the index of the educations array that matches the id
        let index = null
        console.log(`id ${id}`)
        for(let i = 0; i < educations.length; i++) {
            if(educations[i].id == id) {
                console.log('found match')
                index = i
                break
            }
        }
        //then, slice the array to build the new array

        let newEducation = educations[index]
        console.log(newEducation)
        if(checked) {
