import { useEffect, useState } from "react";
import { HeaderModel } from "../model/header";

function HomePage() {

    const [hasHeader, setHasHeader] = useState(false)
    const [header, setHeaderState] = useState({})
    const [hasProfilePhoto, setHasProfilePhoto] = useState(false)
    const [hasEducations, setHasEducations] = useState(false)
    const [hasWorkExperiences, setHasWorkExperiences] = useState(false)
    const [hasPersonalProjects, setHasPersonalProjects] = useState(false)


    const setHeader = async() => {
        let headerModel = new HeaderModel()
        try {
            let header = await headerModel.getHeader()
            setHeaderState(header[0])
            console.log(header[0])
            console.log(hasHeader)
            setHasHeader(true)
        }
        catch (err) {
            setHasHeader(false)
        }
    }

    useEffect(()=> {
        /*
            1. Get Header
                - What to display if there is no header?
                - If no header record is found, a 500 error would be returned. So, I can surround my call to the headerModel's get method 
                with a try/catch, and in the catch, I would set the state of header to 'notFound'. Then, in render, if the state of header is 
                'notFound', then it will display the link to the edit page to edit the header. Repeat for the other sections
            2. Get Profile Photo
            3. Get Educations
            4. Get Work Experiences
            5. Get Personal Projects
        */

        setHeader()


    }, [])

    return (
        <div>
            {hasHeader ? (<HeaderForHomePage></HeaderForHomePage>) : (<h1>{hasHeader.toString()}</h1>)}
        </div>
    )

    function HeaderForHomePage() {
        return (
            <div>
                <h1>Hi, I'm</h1>
                {/* If there is no valid First Name, should I link to the edit header so that the user can edit the header? */}
                {header.firstName != "" && header.firstName != null ? <h2>{header.firstName}</h2> : <a href="/edit?expandedItem=header">First Name</a>}
                {header.lastName != "" && header.lastName != null ? <h2>{header.lastName}</h2> : <a style={{display:"block"}} href="/edit?expandedItem=header">Last Name</a>}
                {header.catchyHeadline != "" && header.catchyHeadline != null ? <h2>{header.catchyHeadline}</h2> : <a style={{display:"block"}} href="/edit?expandedItem=header">Catchy Headline</a>}
                {header.locationCityState != "" && header.locationCityState != null ? <h2>{header.locationCityState}</h2> : <a style={{display:"block"}} href="/edit?expandedItem=header">City, State</a>}
                {header.phoneNumber != "" && header.phoneNumber != null ? <h2>{header.phoneNumber}</h2> : <a style={{display:"block"}} href="/edit?expandedItem=header">Phone Number</a>}



            </div>
        )
    }
}




export default HomePage;
