import React, { useCallback, useState, useEffect } from 'react';
import { useDropzone } from 'react-dropzone';

const ProfilePhotoComponent = ({ existingPhotoUrl, onFileUpload }) => {
    const [file, setFile] = useState(null);

    const onDrop = useCallback(acceptedFiles => {
        // Do something with the files
        setFile(acceptedFiles[0]);
        onFileUpload(acceptedFiles[0]);
    }, [onFileUpload]);

    const { getRootProps, getInputProps } = useDropzone({ onDrop });

    // useEffect(() => {
    //     const getProfilePhoto = async() => {
    //         const status = (await fetch('api/profilePhoto')).status

    //         if(status == 200) {
    //             existingPhotoUrl = 'api/profilePhoto'
    //         }
    //     }

    //     getProfilePhoto()
    // }, [])

    return (
        <div {...getRootProps()} style={dropzoneStyles}>
            <input {...getInputProps()} />
            {existingPhotoUrl && (
                <img
                    src={existingPhotoUrl}
                    alt="Existing"
                    style={{ maxWidth: '100%', maxHeight: '100%' }}
                    onError={(e) => {
                        // Hide the image only if the error is not a 404 status code
                        if (e.target.src === existingPhotoUrl) {
                            e.target.style.display = 'none';
                        }
                    }}
                />
            )}
            {!existingPhotoUrl && (
                <div {...getRootProps()} style={dropzoneStyles}>
                    <input {...getInputProps()} />
                    <p>Drag 'n' drop a photo here, or click to select one</p>
                </div>
            )}
        </div>
    );
};

const dropzoneStyles = {
    position: 'relative',
    border: '2px dashed #ccc',
    borderRadius: '4px',
    padding: '20px',
    textAlign: 'center',
    cursor: 'pointer',
};

const overlayStyles = {
    position: 'absolute',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: 'rgba(255, 255, 255, 0.5)',
    zIndex: 1,
};

export default ProfilePhotoComponent;
