import React, { useCallback, useState, useEffect } from 'react';
import { useDropzone } from 'react-dropzone';

const ProfilePhotoComponent = ({onFileUpload }) => {
    const [file, setFile] = useState(null);


    const [existingPhotoUrl, setExistingPhotoUrl] = useState(null)

    const onDrop = useCallback(acceptedFiles => {
        // Do something with the files
        setFile(acceptedFiles[0]);
        onFileUpload(acceptedFiles[0]);
    }, [onFileUpload]);

    const { getRootProps, getInputProps } = useDropzone({ onDrop });

    useEffect(() => {
        const getProfilePhoto = async() => {
            const status = (await fetch('api/profilePhoto')).status

            if(status == 200) {
                setExistingPhotoUrl('api/profilePhoto')
            }
        }

        getProfilePhoto()
    }, [])

    // return (
    //     <div {...getRootProps()} style={dropzoneStyles}>
    //         <input {...getInputProps()} />
    //         {file || existingPhotoUrl != null ? (
    //             <div style={overlayStyles}>
    //                 {existingPhotoUrl != null && (
    //                     <img src={existingPhotoUrl} alt="Existing" style={{ maxWidth: '100%', maxHeight: '100%' }} />
    //                 )}
    //                 {file && (
    //                     <img src={URL.createObjectURL(file)} alt="Uploaded" style={{ maxWidth: '100%', maxHeight: '100%' }} />
    //                 )}
    //             </div>
    //         ) : (
    //             <p>Drag 'n' drop a photo here, or click to select one</p>
    //         )}
    //     </div>
    // );

    return (
        <div {...getRootProps()} style={dropzoneStyles}>
            <input {...getInputProps()} />
            {file ? (
                <img src={URL.createObjectURL(file)} alt="Uploaded" style={{ maxWidth: '100%', maxHeight: '100%' }} />
            ) : existingPhotoUrl != null ? (
                <img src={existingPhotoUrl} alt="Existing" style={{ maxWidth: '100%', maxHeight: '100%' }} />
            ) : (
                <p>Drag 'n' drop a photo here, or click to select one</p>
            )}
        </div>
    );
};

const dropzoneStyles = {
    position: 'relative',
    border: '2px dashed #ccc',
    borderRadius: '4px',
    padding: '20px',
    textAlign: 'center',
    cursor: 'pointer',
};

const overlayStyles = {
    position: 'absolute',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: 'rgba(255, 255, 255, 0.5)',
    zIndex: 1,
};

export default ProfilePhotoComponent;
